import React, { useState, useEffect } from "react";
import "./App.css";

const App = () => {
  const [search, setSearch] = useState("");
  const [data, setData] = useState([]); // Store backend data
  const [loading, setLoading] = useState(true); // Show loading state
  const [error, setError] = useState(null); // Handle errors

  // Fetch data from backend when the component mounts
  useEffect(() => {
    fetch("http://127.0.0.1:8000/api/items/") // Replace with your actual API endpoint
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to fetch data");
        }
        return response.json();
      })
      .then((backendData) => {
        setData(backendData); // Store fetched data in state
        setLoading(false);
      })
      .catch((error) => {
        setError(error.message);
        setLoading(false);
      });
  }, []);

  // Filter data based on search input
  const filteredData = data.filter(
    (item) =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.category.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div>
      <h1>Item Dashboard</h1>
      <input
        type="text"
        placeholder="Search by name or category..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {/* Show loading message */}
      {loading && <p>Loading data...</p>}

      {/* Show error message if fetch fails */}
      {error && <p style={{ color: "red" }}>Error: {error}</p>}

      {/* Display data only if no errors and not loading */}
      {!loading && !error && (
        <table border="1">
          <thead>
            <tr>
              <th>Name</th>
              <th>Category</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {filteredData.length > 0 ? (
              filteredData.map((item) => (
                <tr key={item.id}>
                  <td>{item.name}</td>
                  <td>{item.category}</td>
                  <td>{item.description}</td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="3" style={{ textAlign: "center", fontWeight: "bold" }}>
                  No items found
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default App;
